    private void rdCremeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdCremeActionPerformed
        // TODO add your handling code here:
        if (rdCreme.isSelected()) {
            rdMorango.setSelected(false);
            rdChocolate.setSelected(false);
        }
    }//GEN-LAST:event_rdCremeActionPerformed

    private void rdMorangoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdMorangoActionPerformed
        // TODO add your handling code here:
        if (rdMorango.isSelected()) {
            rdCreme.setSelected(false);
            rdChocolate.setSelected(false);
        }
    }//GEN-LAST:event_rdMorangoActionPerformed

    private void rdChocolateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rdChocolateActionPerformed
        // TODO add your handling code here:
        if (rdChocolate.isSelected()) {
            rdMorango.setSelected(false);
            rdCreme.setSelected(false);
        }
    }//GEN-LAST:event_rdChocolateActionPerformed

    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        double total;
        double subTotal = 0;
        final double servico = 5.0;
        
        if (rdCreme.isSelected()) {
            subTotal = subTotal + 2;
        } else if (rdMorango.isSelected()) {
            subTotal = subTotal + 2.5;
        } else if (rdChocolate.isSelected()) {
            subTotal = subTotal + 3;
        }
        
        if (ckChocolate.isSelected()) {
            subTotal = subTotal + 0.5;
        }
        if (ckCaramelo.isSelected()) {
            subTotal = subTotal + 0.75;
        }
        if (ckCrocante.isSelected()) {
            subTotal = subTotal + 1.0;
        }
        
        txtSubtotal.setText(String.valueOf(subTotal));
        txtServico.setText(String.valueOf(servico));
        total = subTotal + servico;
        txtTotal.setText(String.valueOf(total));
        
    }//GEN-LAST:event_btnCalcularActionPerformed

