    private void jLabel2MouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel2MouseReleased
        if (player == 1) {
            botaoBranco(evt);
            player = 2;
        } else {
            botaoPreto(evt);
            player = 1;
        }
    }//GEN-LAST:event_jLabel2MouseReleased

    private void jLabel1MouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel1MouseReleased
        if (player == 1) {
            botaoBranco(evt);
            player = 2;
        } else {
            botaoPreto(evt);
            player = 1;
        }
    }//GEN-LAST:event_jLabel1MouseReleased

    public void botaoPreto(MouseEvent evt) {
        int objX = evt.getLocationOnScreen().x;
        int objY = evt.getLocationOnScreen().y;
        System.out.println("X = " + objX);
        System.out.println("Y = " + objY);

        if ((objX >= 3 && objY >= 8) && (objX <= 612 && objY <= 265)) {
            Graphics b1 = jLabel1.getGraphics();
            b1.setColor(Color.BLACK);
            b1.fillOval(x, y, 100, 100);
        } else if (evt.getButton() == MouseEvent.BUTTON2) {
            System.out.println(evt.getLocationOnScreen());
            Graphics b1 = jLabel2.getGraphics();
            b1.setColor(Color.BLACK);
            b1.fillOval(x, y, 100, 100);
        }
    }
