    public void botaoBranco(MouseEvent evt) {
        int objX = evt.getLocationOnScreen().x;
        int objY = evt.getLocationOnScreen().y;
        System.out.println("X = " + objX);
        System.out.println("Y = " + objY);
        if ((objX >= 3 && objY >= 8) && (objX <= 612 && objY <= 265)) {
            Graphics b1 = jLabel1.getGraphics();
            b1.setColor(Color.BLACK);
            b1.fillOval(x, y, 100, 100);
        } else if (evt.getButton() == MouseEvent.BUTTON2) {
            Graphics b1 = jLabel2.getGraphics();
            b1.setColor(Color.BLACK);
            b1.fillOval(x, y, 100, 100);
        }
    }

